import { useEffect, useMemo, useState } from "react";
import { useTheme } from "next-themes";
import { Cloud, fetchSimpleIcons, renderSimpleIcon } from "react-icon-cloud";

const renderCustomIcon = (icon, theme, rotation) => {
  const bgHex = theme === "light" ? "#f3f2ef" : "#080510";
  const fallbackHex = theme === "light" ? "#6e6e73" : "#ffffff";
  const minContrastRatio = theme === "dark" ? 2 : 1.2;

  return renderSimpleIcon({
    icon,
    bgHex,
    fallbackHex,
    minContrastRatio,
    size: 42,
    aProps: {
      href: undefined,
      target: undefined,
      rel: undefined,
      onClick: (e) => e.preventDefault(),
      style: {
        transform: `rotate(${rotation}deg)`,
        transition: "transform 0.3s ease", // Animasi rotasi
      },
    },
  });
};

const cloudProps = {
  containerProps: {
    style: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      width: "100%",
      paddingTop: 40,
    },
  },
  options: {
    reverse: true,
    depth: 1,
    wheelZoom: false,
    imageScale: 2,
    activeCursor: "default",
    tooltip: "native",
    initial: [0.1, -0.1],
    clickToFront: 500,
    tooltipDelay: 0,
    outlineColour: "#0000",
    maxSpeed: 0.04,
    minSpeed: 0.02,
  },
};

export function IconCloud({ iconSlugs }) {
  const [data, setData] = useState(null);
  const { theme } = useTheme();
  const [rotation, setRotation] = useState(0);

  useEffect(() => {
    fetchSimpleIcons({ slugs: iconSlugs }).then(setData);
  }, [iconSlugs]);

  const renderedIcons = useMemo(() => {
    if (!data) return null;

    return Object.values(data.simpleIcons).map((icon, index) => {
      // Hitung rotasi untuk tiap ikon agar membentuk lingkaran
      const angle = (360 / data.simpleIcons.length) * index;
      return renderCustomIcon(icon, theme || "light", angle);
    });
  }, [data, theme, rotation]);

  const handleMouseMove = (e) => {
    const rotationAmount = (e.clientX / window.innerWidth) * 360;
    setRotation(rotationAmount);
  };

  return (
    <Cloud {...cloudProps}>
      <div
        onMouseMove={handleMouseMove}
        className="icon-cloud-container"
        style={{
          position: "relative",
          width: "300px", // Ukuran kontainer yang diinginkan
          height: "300px", // Ukuran kontainer yang diinginkan
        }}
      >
        {renderedIcons}
      </div>
    </Cloud>
  );
}

export const Skills = () => {
  const slugs = [
    "typescript",
    "javascript",
    "react",
    "html5",
    "css3",
    "nodedotjs",
    "express",
    "nextdotjs",
    "vercel",
    "git",
    "github",
    "gitlab",
    "visualstudiocode",
    "int"
  ];

  return (
    <section className="skill" id="skills">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h2>Skills</h2>
              <p>
                Lorem Ipsum is simply dummy text of the printing and typesetting
                industry.
                <br /> Lorem Ipsum has been the industry's standard dummy text.
              </p>
              <div className="relative flex size-full max-w-lg items-center justify-center overflow-hidden rounded-lg border-transparent bg-background px-20 pb-20 pt-8">
                <IconCloud iconSlugs={slugs} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
