import "react-multi-carousel/lib/styles.css";
import colorSharp from "../assets/img/color-sharp.png";
import meter1 from "../assets/img/meter1.svg";
Cloudarr
export const About = () => {
  const responsive = {
    superLargeDesktop: {
      // the naming can be any, depends on you.
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };

  return (
    <section className="about" id="about">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="about-bx wow zoomIn">
              <h2>About</h2>
              <p>
                Hello! I’m Diaz Eka Cipta, a recent graduate with a Information
                Technology from University Bina Sarana Infromatika. I am
                passionate about technology and eager to leverage my academic
                background and hands-on experience to contribute to innovative
                projects and solve real-world challenges. <br /> Hello! I’m Diaz
                Eka Cipta, a recent graduate with a Information Technology from
                University Bina Sarana Infromatika. I am passionate about
                technology and eager to leverage my academic background and
                hands-on experience to contribute to innovative projects and
                solve real-world challenges.
              </p>
              <div className="item">
                <img src={meter1} alt="Image" />
                <div>
                  <button variant="success" onClick={downloadResume}>
                    Download resume here! <CloudArrowDownFill size={25} />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <img className="background-image-left" src={colorSharp} alt="Image" />
    </section>
  );
};
