import { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import headerImg from "../assets/img/header-img.svg";
import { ArrowRightCircle } from "react-bootstrap-icons";
import "animate.css";
import TrackVisibility from "react-on-screen";
import { CloudArrowDownFill } from "react-bootstrap-icons";

export const Banner = () => {
  const [loopNum, setLoopNum] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [text, setText] = useState("");
  const [delta, setDelta] = useState(300 - Math.random() * 100);
  const [index, setIndex] = useState(1);
  const toRotate = ["Front-End Web Developer"];
  const period = 2000;


  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, delta);

    return () => {
      clearInterval(ticker);
    };
  }, [text]);

  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting
      ? fullText.substring(0, text.length - 1)
      : fullText.substring(0, text.length + 1);

    setText(updatedText);

    if (isDeleting) {
      setDelta((prevDelta) => prevDelta / 2);
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setIndex((prevIndex) => prevIndex - 1);
      setDelta(period);
    } else if (isDeleting && updatedText === "") {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setIndex(1);
      setDelta(500);
    } else {
      setIndex((prevIndex) => prevIndex + 1);
    }
  };
 
  function App() {
    const downloadResume = () => {
      // Path ke file resume Anda
      const resumeUrl = '/path/to/your/resume.pdf'; // Ganti dengan path file resume Anda
      const link = document.createElement('a');
      link.href = resumeUrl;
      link.download = 'resume.pdf'; // Nama file yang akan diunduh
      link.click();
    };
  return (
    <section className="banner" id="home">
      <Container>
        <Row className="aligh-items-center">
          <Col xs={12} md={6} xl={7}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div
                  className={
                    isVisible ? "animate__animated animate__fadeIn" : ""
                  }
                >
                  <span className="tagline">Welcome to my Portfolio</span>
                  <h1>
                    {`Hi! I'm Cipta`}
                    {""}
                    <span
                      className="txt-rotate"
                      dataPeriod="1000"
                      data-rotate='[ "Front-End Developer"]'
                    >
                      <span className="wrap">{text}</span>
                    </span>
                  </h1>
                  <p>
                    Hello! I’m Diaz Eka Cipta, a recent graduate with a
                    Information Technology from University Bina Sarana
                    Infromatika. I am passionate about technology and eager to
                    leverage my academic background and hands-on experience to
                    contribute to innovative projects and solve real-world
                    challenges.
                    <br /> I am particularly excited about opportunities in Web
                    Developper and am keen to work with a dynamic team that
                    values innovation and continuous learning. 
                  </p>

                  <a
                    href="mailto:diazekacipta467@gmail.com?subject=Hello&body=I%20would%20like%20to%20talk%20with%20you."
                    className="text-decoration-none"
                  >
                    <button>
                      Let’s Talk! <ArrowRightCircle size={25} />
                    </button>
                  </a>
                  <button>
                    Download resume here! <CloudArrowDownFill size={25} />
                  </button>
                </div>
              )}
            </TrackVisibility>
          </Col>
          <Col xs={12} md={6} xl={5}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div
                  className={
                    isVisible ? "animate__animated animate__zoomIn" : ""
                  }
                >
                  <img src={headerImg} alt="Header Img" />
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
}
