"use client";

import { useEffect, useMemo, useState } from "react";
import { useTheme } from "next-themes";
import { Cloud, fetchSimpleIcons, renderSimpleIcon } from "react-icon-cloud";

// Fungsi untuk merender ikon dengan kustomisasi berdasarkan tema
const renderCustomIcon = (icon, theme, rotation) => {
  const bgHex = theme === "light" ? "#f3f2ef" : "#080510";
  const fallbackHex = theme === "light" ? "#6e6e73" : "#ffffff";
  const minContrastRatio = theme === "dark" ? 2 : 1.2;

  return renderSimpleIcon({
    icon,
    bgHex,
    fallbackHex,
    minContrastRatio,
    size: 42,
    aProps: {
      href: undefined,
      target: undefined,
      rel: undefined,
      onClick: (e) => e.preventDefault(),
      style: {
        transform: `rotate(${rotation}deg)`, // Rotasi berdasarkan state
        transition: "transform 0.3s ease", // Animasi saat rotasi
      },
    },
  });
};

// Properti untuk komponen Cloud
const cloudProps = {
  containerProps: {
    style: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      width: "100%",
      paddingTop: 40,
    },
  },
  options: {
    reverse: true,
    depth: 1,
    wheelZoom: false,
    imageScale: 2,
    activeCursor: "default",
    tooltip: "native",
    initial: [0.1, -0.1],
    clickToFront: 500,
    tooltipDelay: 0,
    outlineColour: "#0000",
    maxSpeed: 0.04,
    minSpeed: 0.02,
  },
};

export function IconCloud({ iconSlugs }) {
  const [data, setData] = useState(null);
  const { theme } = useTheme();
  const [rotation, setRotation] = useState(0); // State untuk rotasi

  useEffect(() => {
    fetchSimpleIcons({ slugs: iconSlugs }).then(setData);
  }, [iconSlugs]);

  // Menyesuaikan ikon berdasarkan tema dan rotasi
  const renderedIcons = useMemo(() => {
    if (!data) return null;

    return Object.values(data.simpleIcons).map((icon) =>
      renderCustomIcon(icon, theme || "light", rotation)
    );
  }, [data, theme, rotation]);

  // Event saat mouse bergerak di atas ikon
  const handleMouseMove = (e) => {
    const rotationAmount = (e.clientX / window.innerWidth) * 360; // Hitung rotasi berdasarkan posisi horizontal mouse
    setRotation(rotationAmount); // Update rotasi berdasarkan posisi mouse
  };

  return (
    <Cloud {...cloudProps}>
      <div
        onMouseMove={handleMouseMove} // Pasang event listener untuk mouse move
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          width: "100%",
        }}
      >
        {renderedIcons}
      </div>
    </Cloud>
  );
}

// Kode untuk IconCloudDemo
const slugs = [
  "typescript",
  "javascript",

  "react",

  "html5",
  "css3",
  "nodedotjs",
  "express",
  "nextdotjs",
  "prisma",
  "amazonaws",
  "postgresql",
  "firebase",
  "nginx",
  "vercel",
  "testinglibrary",
  "jest",
  "cypress",
  "docker",
  "git",
  "jira",
  "github",
  "gitlab",
  "visualstudiocode",
  "androidstudio",
  "sonarqube",
  "figma",
];

export const Skills = () => {
 
  return (
    <div className="relative flex size-full max-w-lg items-center justify-center overflow-hidden rounded-lg border-transparent bg-background px-20 pb-20 pt-8">
     
      <p>lorem ipsum</p>
      <IconCloud iconSlugs={slugs} />
    </div>
  );
};
