//                 <div className="item">
//                   <img src={meter1} alt="Image" />
//                   <h5>Web Development</h5>
//                 </div>
//                 <div className="item">
//                   <img src={meter2} alt="Image" />
//                   <h5>Brand Identity</h5>
//                 </div>
//                 <div className="item">
//                   <img src={meter3} alt="Image" />
//                   <h5>Logo Design</h5>
//                 </div>
//                 <div className="item">
//                   <img src={meter1} alt="Image" />
//                   <h5>Web Development</h5>
