import { useEffect, useMemo, useState } from "react";
import { useTheme } from "next-themes";
import { Cloud, fetchSimpleIcons, renderSimpleIcon } from "react-icon-cloud";

const renderCustomIcon = (icon, theme, rotation) => {
  const bgHex = theme === "light" ? "#f3f2ef" : "#080510";
  const fallbackHex = theme === "light" ? "#6e6e73" : "#ffffff";
  const minContrastRatio = theme === "dark" ? 2 : 1.2;

  return renderSimpleIcon({
    icon,
    bgHex,
    fallbackHex,
    minContrastRatio,
    size: 42,
    aProps: {
      href: undefined,
      target: undefined,
      rel: undefined,
      onClick: (e) => e.preventDefault(),
      style: {
        transform: `rotate(${rotation}deg)`,
        transition: "transform 0.3s ease", // Animasi rotasi
      },
    },
  });
};

const cloudProps = {
  containerProps: {
    style: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      width: "100%",
      paddingTop: 40,
    },
  },
  options: {
    reverse: true,
    depth: 1,
    wheelZoom: false,
    imageScale: 2,
    activeCursor: "default",
    tooltip: "native",
    initial: [0.1, -0.1],
    clickToFront: 500,
    tooltipDelay: 0,
    outlineColour: "#0000",
    maxSpeed: 0.04,
    minSpeed: 0.02,
  },
};

export function IconCloud({ iconSlugs }) {
  const [data, setData] = useState(null);
  const { theme } = useTheme();

  useEffect(() => {
    fetchSimpleIcons({ slugs: iconSlugs }).then(setData);
  }, [iconSlugs]);

  // Menyusun ikon dalam formasi melingkar
  const renderedIcons = useMemo(() => {
    if (!data) return null;

    // Tentukan jarak antar ikon (radius) dan hitung sudut untuk formasi melingkar
    const radius = 80; // Menentukan radius lingkaran
    const angleStep = (2 * Math.PI) / data.simpleIcons.length; // Menghitung langkah sudut

    return Object.values(data.simpleIcons).map((icon, index) => {
      // Hitung posisi untuk tiap ikon
      const angle = angleStep * index;
      const x = radius * Math.cos(angle); // Posisi horizontal (X)
      const y = radius * Math.sin(angle); // Posisi vertikal (Y)

      return (
        <div
          key={icon}
          className="icon-item"
          style={{
            transform: `translate(${x}px, ${y}px)`, // Posisikan tiap ikon dalam lingkaran
          }}
        >
          {renderCustomIcon(icon, theme || "light", 0)} {/* Render ikon */}
        </div>
      );
    });
  }, [data, theme]);

  return (
    <Cloud {...cloudProps}>
      <div
        className="icon-cloud-container"
        style={{
          position: "relative",
          width: "200px", // Ukuran kontainer
          height: "200px", // Ukuran kontainer
        }}
      >
        {renderedIcons}
      </div>
    </Cloud>
  );
}

export const Skills = () => {
  const slugs = [
    "typescript",
    "javascript",
    "react",
    "html5",
    "css3",
    "nodedotjs",
    "express",
    "nextdotjs",
    "vercel",
    "git",
    "gitlab",
    "visualstudiocode",
    "mongodb",
    "github",
  ];

  return (
    <section className="skill  bg-[url('/src/assets/')] bg-cover bg-center" id="skills">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h2>Skills</h2>
              <p>
                Lorem Ipsum is simply dummy text of the printing and typesetting
                industry.
                <br /> Lorem Ipsum has been the industry's standard dummy text.
              </p>
              <div className="relative flex size-full max-w-lg items-center justify-center overflow-hidden rounded-lg border-transparent bg-background px-20 pb-20 pt-8">
                <IconCloud iconSlugs={slugs} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
